import React, {useState} from 'react';
import './signupComponent.css';
import axios from 'axios';
import {useHistory} from 'react-router-dom';
const SignupComponent = () => {
  const history = useHistory();
  const [Username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirnm] = useState('');

  const handelclick = (e) => {
    e.preventDefault();

    var data = {
      name: Username,
      email: email,
      password: password,
      confirmPassword: confirm,
    };

    axios
      .post('http://localhost:8083/api/v1/user/signUp', data)
      .then((res) => {
        if (res.data.success === 1) {
          history.push('/login');
        } else if (res.data.success === 0) {
          alert(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div class="container-fluid signup">
      {' '}
      <div class="row">
        {' '}
        <div class="col-md-6">
          {' '}
          <div>
            {' '}
            <form class="box" onSubmit={handelclick}>
              {' '}
              <h1>Sign Up</h1>{' '}
              <input
                type="text"
                name=""
                placeholder="Username"
                value={Username}
                onChange={(e) => setUsername(e.target.value)}
              />{' '}
              <input
                type="text"
                name=""
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />{' '}
              <input
                type="password"
                name=""
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />{' '}
              <input
                type="confirm password"
                name=""
                placeholder="Password"
                value={confirm}
                onChange={(e) => setConfirnm(e.target.value)}
              />{' '}
              <input type="submit" name="" value="Sign Up" href="#" />{' '}
              <p>Already have an acoount?</p>
              <a class="forgot text-muted" href="#">
                Sign In
              </a>{' '}
              <div class="col-md-12">
                {' '}
                <ul class="social-network social-circle">
                  {' '}
                  <li>
                    <a href="#" class="icoFacebook" title="Facebook">
                      <i class="fab fa-facebook-f"></i>
                    </a>
                  </li>{' '}
                  <li>
                    <a href="#" class="icoTwitter" title="Twitter">
                      <i class="fab fa-twitter"></i>
                    </a>
                  </li>{' '}
                  <li>
                    <a href="#" class="icoGoogle" title="Google +">
                      <i class="fab fa-google-plus"></i>
                    </a>
                  </li>{' '}
                </ul>{' '}
              </div>{' '}
            </form>{' '}
          </div>{' '}
        </div>{' '}
      </div>
    </div>
  );
};

export default SignupComponent;
